	Matrixp SpeechFeatures::magspec(Matrixp framesj, int NFFT) {

		if (framesj->getColumn() > NFFT) {
			//std::stringstream ss;
			//ss << "frame length " << framesj->getColumn() << " is greater than FFT size " << NFFT << ", frame will be truncated. Increase NFFT to avoid.";
			//throw std::exception(ss.str().c_str());
		}
		int framesj_rows_count = framesj->getRow();	
		int N = NFFT / 2;
		int Nsize = N + 1;
		Matrixp complex_spec = create_matrixp(Nsize, framesj_rows_count);
		for (int i = 0; i < framesj_rows_count; ++i) {
			Vectorp w = framesj->getRow(i);
			Vectorp b = create_verctorp(NFFT - w->getSize());
			Vectorp c = w->concatenate(b);			
			rfft(c->getData(), NFFT);
			Vectorp absf = create_verctorp(Nsize);
			absf->set(0, abs(c->get(0)));		
			for (int i = 1; i < N; ++i) {				
				absf->set(i, sqrt(c->get(i) * c->get(i) + c->get(NFFT - i) * c->get(NFFT - i)));
			}
			absf->set(N, abs(c->get(N)));
			complex_spec->setRow(i, absf);
		}		
		return complex_spec;
	}

	Vectorp SpeechFeatures::preemphasis(Vectorp signal, accuracy coeff) {		
		return Vector::concatenate(signal->get(0), signal->skip(1)->subtract(signal->skip(-1)->multiply(coeff)));
	}
	Matrixp SpeechFeatures::framesig(Vectorp sig, accuracy frame_len, accuracy frame_step, void * winfunc)
	{	
		int slen = sig->getSize();
		frame_len = (accuracy)round(frame_len);
		frame_step = (accuracy)round(frame_step);
		int numframes;
		if (slen <= frame_len) {
			numframes = 1;
		}
		else {
			numframes = (int)(1 + ceil((1.0*slen - frame_len) / frame_step));
		}
		int padlen = (int)((numframes - 1)*frame_step + frame_len);
		Vectorp zeros = create_verctorp(padlen - slen);
		Vectorp padsignal = sig->concatenate(zeros);			
		Vectorp tileArg1 = Vector::arange(0, frame_len);
		Vectorp tileArg2 = Vector::arange(0, numframes*frame_step, frame_step);				
		Matrixp tile1 = tile(tileArg1, create_verctorp_by_array(2, (accuracy)numframes, 1.0));
		Matrixp tile2 = tile(tileArg2, create_verctorp_by_array(2, (accuracy)frame_len, 1.0 ))->T();		
		Matrixp indices = tile1->add(tile2);		
		Matrixp framesj = array_extract(padsignal, indices);		
		return framesj;
	}

	Matrixp SpeechFeatures::tile(Vectorp a, Vectorp reps)
	{
		int aLen = a->getSize();
		int reps0 = (int)(reps->get(0));
		if (reps->get(1) != 1) {
			//throw std::exception("��֧��reps Ϊ (N, 1)�����");
		}
		Matrixp b = a->concatenate(a);			
		for (int i = 0; i < reps0 - 2; i++) {
			b = b->concatenate(a);				
		}			
		return b->reshape(reps0, aLen);
	}	

